mapper.select:
select

from task_alert


        and task_id = #{taskId}


        and execution_id = #{executionId}


        and alert_type = #{alertType}


        and status = #{status}


        and send_time >= #{sendTime}

    and del_flag = 0

order by create_time desc

mapper.insert:
insert into task_alert (
    task_id, execution_id, alert_type, receiver, content, status,
    send_time, send_result, create_by, update_by, remark
) values (
    #{taskId}, #{executionId}, #{alertType}, #{receiver}, #{content}, #{status},
    #{sendTime}, #{sendResult}, #{createBy}, #{updateBy}, #{remark}
)
